method.body:

            var XUL_NS = "http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul";
            function newelem(tagname) { return document.createElementNS(XUL_NS, tagname); }
	    var list = document.getAnonymousElementByAttribute(this, "anonid", "tracklist");
            var tracks = 0;

            function doappend(title)
            {
              var row = newelem("listitem");
              row.appendChild(newelem("listcell")).setAttribute("label", ++tracks);
              row.appendChild(newelem("listcell")).setAttribute("label", title);
              list.appendChild(row);
            }

            titles.forEach(doappend);
            list.setAttribute("rows", Math.min(5, titles.length));
