implementation.constructor:
// Dont trap mouseclicks
this.setAttribute('mousethrough', 'always');

// If you start maximized, prevent dragging.
var titlebar = document.getAnonymousElementByAttribute(this, 'sbid', 'draggable-titlebar');
if ( document.documentElement.getAttribute( "sizemode" ) == "maximized" ) {
  titlebar.setAttribute("hidden", "true");
}

// Pay attention to if the window changes state
var self = this;
this._windowmutation = function(evt) {
  self.onWindowMaximize(evt);
};
window.addEventListener("DOMAttrModified", this._windowmutation, true);

implementation.destructor:
// Remove the crappycrap
window.removeEventListener("DOMAttrModified", this._windowmutation, true);
this._windowmutation = null;

method.body:

// If the sizemode changed...
if ( evt.attrName == "sizemode" ) {
  var mode = document.documentElement.getAttribute( evt.attrName );
  var titlebar = document.getAnonymousElementByAttribute(this, 'sbid', 'draggable-titlebar');
  if ( mode == "maximized" ) {
    // Hide the titlebar (prevent drag) if we are now maximized
    titlebar.setAttribute("hidden", "true");
  }
  else {
    // Show the titlebar (allow drag) if we are not maximized
    titlebar.removeAttribute("hidden");
  }
}
