implementation.constructor:

this.boxid = "growbox."+this.getAttribute("id");
// drawers that flex need to set the property of their flex-able hbox parent.
// Find that element by using our hflex attribute which contains the id of
// the flex-able hbox.
var hflexId = this.getAttribute("hflex");
if (hflexId) {
  this.hflex = document.getElementById(hflexId);
  if (this.hflex)
    this.hflex.setAttribute("flex", "0");
}
if (this.getAttribute("header") == "false") {
  this.header.setAttribute("hidden", "true");
}
if (this.getAttribute("border") == "false") {
  this.parent.setAttribute("class", "");
}

method.body:

try
{
  if (this.state == "closed") this.openDrawer();
  else this.closeDrawer();
}
catch ( err )
{
  alert( "drawer.xml - toggleDrawer - " + err );
}


try
{
  if (this.state == "open") return;
  if (!replacedheight) replacedheight = 0;
  var size=this.getAttribute("contentheight");
  if (!size) {
    try {
      size = this.getContentSize();
    } catch (e) { }
  }
  this.size = Math.max(size, replacedheight);
  // No animations until we finish
  if ( ! this.tracking )
  {
    this.state = "open";
    if (this.openclosebutton) this.openclosebutton.label = "-";

    if (this.hflex) this.hflex.setAttribute("flex", "1");

    // Make the growbox
    this.growbox = document.createElement( "box" );
    this.growbox.setAttribute( "flex", "1" );
    this.growbox.setAttribute( "sbid", this.boxid );
    this.parent.insertBefore( this.growbox, this.before );

    // Set our animation values
    this.tracking = true;
    this.distance = this.size - replacedheight;
    this.current = 0;
    this.anim_start = now ? -9999 : new Date().getTime();
    this.anim_time = 0.2;
    this.anim_complete = this.onGrowComplete;
    this.anim_complete_event = "opendrawer";

    // Start our animation loop
    this.interval = setInterval(function(obj) { obj.animationCallback(); }, 0, this); 
  }
}
catch ( err )
{
  alert( "drawer.xml - openDrawer - " + err );
}


try
{
  if (this.state == "closed") return;
  if (!replacedheight) replacedheight = 0;
  var size=this.getAttribute("contentheight");
  if (!size) {
    try {
      size = this.getContentSize();
    } catch (e) { }
  }
  this.size = Math.max(size, replacedheight);
  this.state = "closed";
  if (this.openclosebutton) this.openclosebutton.label = "+";
  // No animations until we finish
  if ( ! this.tracking )
  {
   if (this.hflex) this.hflex.setAttribute("flex", "1");

    // Make the growbox
    this.growbox = document.createElement( "box" );
    this.growbox.setAttribute( "flex", "1" );
    this.growbox.setAttribute( "sbid", this.boxid );
    this.parent.insertBefore( this.growbox, this.content );

    // Remove the content
    this.parent.removeChild( this.content );
    this.content = null;

    try 
    {
      this.onContentRemoved();
    }
    catch (e) { }

    // Set our animation values
    this.tracking = true;
    this.distance = - (this.size-replacedheight); // removing!
    this.current = 0;
    this.anim_start = now ? -9999 : new Date().getTime();
    this.anim_time = 0.2;
    this.anim_complete = this.onShrinkComplete;
    this.anim_complete_event = "closedrawer";

    // Start our animation loop
    this.interval = setInterval(function(obj) { obj.animationCallback(); }, 0, this); 
    }
}
catch ( err )
{
  alert( "drawer.xml - closeDrawer - " + err );
}


try
{
  if ( this.tracking )
  {
    // Calculate size to resize window based on timer interpolation
    var elapsed = ( new Date().getTime() - this.anim_start ) / 1000;
    var interpolate = elapsed / this.anim_time;
    var size = parseInt( ( this.distance * interpolate ) - this.current + 0.5 );

    // We have to track a "current" because we do not resize the window via absolute values
    if ( Math.abs( this.current + size ) > Math.abs( this.distance ) )
      size = this.distance - this.current;

    if (size != 0) {
      window.resizeBy( 0, size );
      this.current += size;
    }

    // Are we done?
    if ( this.current == this.distance )
    {
      if ( this.anim_complete )
      {
        this.anim_complete( );
      }

      var e = document.createEvent("Events");
      e.initEvent(this.anim_complete_event, false, true);
      this.dispatchEvent(e);

      // Set our values back
      this.tracking = false;
      this.distance = 0;
      this.current = 0;
      clearInterval( this.interval );
      this.interval = null;
    }
  }
  else
  {
    clearInterval( this.interval );
    this.interval = null;
  }
}
catch ( err )
{
  alert( "drawer.xml - animationCallback - " + err );
}


try
{
  // Replace the grow box with the content item
  if ( this.growbox )
  {
    this.insertContent( this.growbox );
    this.parent.removeChild( this.growbox );
  }
  if (this.hflex) this.hflex.setAttribute("flex", "0");
}
catch ( err )
{
  alert( "drawer.xml - onGrowComplete - " + err );
}


try
{
  // Replace the grow box with the content item
  if ( this.growbox )
  {
    this.parent.removeChild( this.growbox );
  }
  if (this.hflex) this.hflex.setAttribute("flex", "0");
}
catch ( err )
{
  alert( "drawer.xml - onShrinkComplete - " + err );
}


try
{
  this.content = this.insertDrawerContent();
  this.parent.insertBefore( this.content, this.before );
  try 
  {
    this.onContentInserted();
  }
  catch (e) { }
}
catch ( err )
{
  alert( "drawer.xml - insertContent - " + err );
}


try
{
  // Make the extension droplist
  var description = document.createElement( "description" );
  description.textContent = this.getAttribute("value");
  return description;
}
catch ( err )
{
  alert( "insertDrawerContent - " + err );
}
return null;


try
{
  var height=this.getAttribute("contentheight");
  if (!height) {
    try {
      height = this.getContentSize();
    } catch (e) { }
  }
  var ret = document.createElement("iframe");
  ret.setAttribute("src", this.getAttribute("uri"));
  ret.setAttribute("height", height);
  return ret;
}
catch ( err )
{
  alert( "contentdrawer - insertDrawerContent - " + err );
}
return null;
