implementation.constructor:

// NOTE: this would be much nicer in the base class but their constructor fires first!
// We need to explicitly call onPopupShowing() here since the first
// time this constructor is called it is after the popupshowing event
// has fired.
this.onPopupShowing();

method.body:

var ml = gBrowser.mediaListPage.mediaListView.mediaList;

// Make a disabled menuitem to act as a label above the page items
var item = document.createElement("menuitem");
  item.setAttribute("label", ml.getProperty(SBProperties.mediaListName));
  item.setAttribute("name", "feathers.item");
  item.setAttribute("disabled", "true");
  item.setAttribute("class", "feathers-menu-selected-skin-title");
this._addToMenu(item);

var mgr = Components.classes["@songbirdnest.com/Songbird/MediaListPageManager;1"]
              .getService(Components.interfaces.sbIMediaListPageManager);
var layouts = mgr.getAvailablePages(ml);

// Attach a menuitem for each compatible layout
while (layouts.hasMoreElements()) {
  var layout = layouts.getNext().QueryInterface(Components.interfaces.sbIMediaListPageInfo);

  var item = this._makeMenuItem(layout);

  // TODO: for demo
  if (item.url == "TODO") {
    item.setAttribute("checked", "true");
  }
  else {
    item.setAttribute("checked", "false");
  }

  item.setAttribute("class", "feathers-menu-selected-skin");

  this._addToMenu(item);
}

// If the current window has a browser/servicepane, then add a link
// to get more feathers.
// NOTE: This is brittle. Please change this once we have a proper API.
if (typeof(gBrowser) != "undefined") {
  this._addToMenu(document.createElement("menuseparator"));
  this._addToMenu(this._makeGetMore());
}


var item = document.createElement("menuitem");
item.setAttribute("label", layout.contentTitle);
item.setAttribute("name", "feathers.item");
item.setAttribute("layoutURL", layout.contentUrl);
item.setAttribute("type", "radio");

item.onCommand = function() {
  gBrowser.loadURI(layout.contentUrl, null, null, null, '_top', gBrowser.mediaListPage.mediaListView);
}
return item;


var item = document.createElement("menuitem");

var getMoreText;
try {
  getMoreText = this._stringBundle.GetStringFromName("feathers.getmore");
} catch (e) { 
  getMoreText="Error";
  dump("\n\n\nfeathers.xml:  Error getting menu text from songbird.properties" +
       " bundle\n"+ e + "\n"); 
}

item.setAttribute("label", getMoreText);
item.setAttribute("name", "menu.getmore");
item.onCommand = function() { gBrowser.top.openLink("http://addons.songbirdnest.com"); }
return item;
