    if ( this._commands ) // I hope so!
    {
      this._commands.onCommand( evt );
    }
  }
}
catch( err )
{
  alert( "playlist.xml - onPlaylistContextMenu - " + err );
}


try
{
  this._commandmenuitems.setDeferRefresh();
}
catch( err )
{
  alert( "playlist.xml - onPlaylistContextMenu - " + err );
}


try
{
  this._commandmenuitems.clearDeferRefresh();
}
catch( err )
{
  alert( "playlist.xml - onPlaylistContextMenu - " + err );
