var dnd = this._Cc["@songbirdnest.com/Songbird/DndSourceTracker;1"]
                  .getService(this._Ci.sbIDndSourceTracker);

var data = this._getTransferDataForFlavor(this._TYPE_X_SB_TRANSFER_MEDIA_ITEM);
